
public class Sample01_escape {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("aaa\tbbb"); // 탭
		System.out.println("aaa\nbbb"); // 줄바꿈
		System.out.println("aaa\'bbb"); // ' 출력
		System.out.println("aaa\"bbb"); // " 출력
		System.out.println("aaa\\bbb"); // \ 출력
		
	} // end main

} // end class
